import { toHaveDisplayValue } from "@testing-library/jest-dom/dist/matchers";
import React, { Component } from "react";
import styled from "styled-components";
import logo from "./hp-logo.png";

export const Calc = styled.section`
  @import url("https://fonts.googleapis.com/css2?family=Inconsolata:wght@400;500;700;900&display=swap");

  background-image: url('.bg.png');
  width: 20%;
  margin: 0 auto;
  padding: 1rem 2rem;
  display: flex;
  flex-direction: column;
  align-items: center;

  div:first-of-type{
    width: 100%;
    display: flex;
    align-items: center;
    justify-content: space-between;
  }

  h1 {
    font-family: "Inconsolata", monospace;
    font-size: 3rem;
    color: black;
    text-shadow: 
    #FFF 0px 0px 5px, 
    #FFF 0px 0px 10px, 
    #FFF 0px 0px 15px, 
    #FF2D95 0px 0px 20px, 
    #FF2D95 0px 0px 30px, 
    #FF2D95 0px 0px 40px, 
    #FF2D95 0px 0px 50px, 
    #FF2D95 0px 0px 75px;
  }

  img {
    width: 2vw;
  }

  input{
    width: 60%;
    font-size: 2rem;
    font-family: "Inconsolata", monospace;
    font-weight: 700;
    margin-bottom: 1rem;
    text-align: center;
    outline: none;
    border: none;
  }

  input[type="number"]::-webkit-inner-spin-button{
    -webkit-appearance: none;
  }

  div:last-of-type{
    width: 100%;
    display: flex;
    justify-content: space-between;
  }

  button{
    font-family: "Inconsolata", monospace;
    font-size: 1.5rem;
    border-radius: 30% 70% 70% 30% / 30% 30% 70% 70%;
    box-shadow: 
    inset 2px 2px 2px 2px #1C6EA4, 
    inset 11px 11px 2px 0px #2285C7, 
    inset 20px 20px 2px 1px #289DEA, 
    inset 29px 29px 2px 1px #57BBEA, 
    5px 5px 15px 5px rgba(0,0,0,0);
    background-color: purple;
    padding: .5em 1em;
    border: none;
    cursor: pointer;
  }

  button:last-of-type{
    background-color: red;
    color: black;
  }

  h2{
    width: 100%;
    font-family: "Inconsolata", monospace;
    font-size: 3rem;
    background-color: white;
    color: black;
    text-align: center;
    text-shadow: 
    #FFF 0px 0px 5px, 
    #FFF 0px 0px 10px, 
    #FFF 0px 0px 15px, 
    #FF2D95 0px 0px 20px, 
    #FF2D95 0px 0px 30px, 
    #FF2D95 0px 0px 40px, 
    #FF2D95 0px 0px 50px, 
    #FF2D95 0px 0px 75px;
  }
`;

export default class App extends Component {

  state = {
    numberOne: "",
    numberTwo: "",
    result: ""
  };

  handleChangeOne = (e) => {
    this.setState({
      numberOne: Number(e.target.value)
    })
  };

  handleChangeTwo = (e) => {
    this.setState({
      numberTwo: Number(e.target.value)
    })
  };

  soma = (e) => {
    this.setState({
      result: this.state.numberOne + this.state.numberTwo
    })
  };

  less = () => {
    this.setState({
      result: this.state.numberOne - this.state.numberTwo
    })
  };

  multi = () => {
    this.setState({
      result: this.state.numberOne * this.state.numberTwo
    })
  };

  div = () => {
    this.setState({
      result: this.state.numberOne / this.state.numberTwo
    })
  };

  clear = () => {
    this.setState({
      result: 0
    })
  };

  render(){
    return (
      <Calc>
        <div>
          <img src={logo} alt="logo" />
          <h1>Calculadora</h1>
          <img src={logo} alt="logo" />
        </div>
        <input type="number" onChange={this.handleChangeOne}/>
        <input type="number" onChange={this.handleChangeTwo}/>
        <div>
          <button onClick={this.div}>/</button>
          <button onClick={this.multi}>*</button>
          <button onClick={this.less}>-</button>
          <button onClick={this.soma}>+</button>
          <button onClick={this.clear}>C</button>
        </div>
        <h2>{this.state.result}</h2>
      </Calc>
    );
  }
}


