import React, { Component } from "react";
import styled from "styled-components";
import logo from "./hp-logo.png";

export const Calc = styled.section`
  @import url("https://fonts.googleapis.com/css2?family=Inconsolata:wght@400;500;700;900&display=swap");

  h1 {
    color: black;
    font-family: "Inconsolata", monospace;
    font-size: clamp(1.5rem, 2vw, 3rem);
  }

  img {
    width: 3vw;
  }
`;

export default class App extends Component {

  state = {
    neutral: 0,
    numberOne: 0,
    numberTwo: 0,
    calcHolder: "",
    result: "",
  };

  // inputNumber = (e) => {
  //   let inputNum = e.target.value;
  //   if(inputNum === 0){
  //     this.setState ({
  //       neutral: inputNum
  //   })}else{
  //     this.setState ({
  //       neutral: inputNum + inputNum
  //   })
  //   }
  // }

  inputNumber = (e) => {
    if (this.state.neutral === 0){
      this.setState ({
        neutral: e.target.value
      })}else{
        this.setState ({
          neutral: this.state.neutral + e.target.value
        })
    }
  }

  inputValueOne = (e) => {
    this.setState({
      numberOne: e.target.value
    });
  }


  soma = () => {
    this.setState({
      calcHolder:
        parseFloat(this.state.numberOne) + 
        parseFloat(this.state.numberTwo)
    });
  };

  less = () => {
    this.setState({
      result: this.state.numberOne - this.state.numberTwo
    });
  };

  multi = () => {
    this.setState({
      result: this.state.numberOne * this.state.numberTwo
    });
  };

  div = () => {
    this.setState({
      result: parseFloat(this.state.numberOne) / 
      parseFloat(this.state.numberTwo)
    });
  };

  clear = () => {
    this.setState({
      neutral: 0
    });
  };

  wrapper = () => ({
    return: this.state.calcHolder
  })

  handleChangeOne = (e) => {
    this.setState({
      numberOne: e.target.value
    });
  };

  handleChangeTwo = (e) => {
    this.setState({
      numberTwo: e.target.value
    });
  };

  render() {
    return (
      <Calc>
        <div>
          <img src={logo} alt="logo" />
          <h1>Calculadora</h1>
        </div>
        {/* <input type="number" onChange={this.handleChangeOne}/>
        <input type="number" onChange={this.handleChangeTwo}/> */}
        <h2>{this.state.neutral}</h2>
        <button onClick={this.inputNumber} value={7}>7</button>
        <button onClick={this.inputNumber} value={8}>8</button>
        <button onClick={this.soma}>+</button>
        <button onClick={this.clear}>C</button>
        <button onClick={this.wrapper}>=</button>
        <button onClick={this.div}>/</button>
      </Calc>
    );
  }
}
