import { toHaveDisplayValue } from "@testing-library/jest-dom/dist/matchers";
import React, { Component } from "react";
import styled from "styled-components";
import logo from "./hp-logo.png";

export const Calc = styled.section`
  @import url("https://fonts.googleapis.com/css2?family=Inconsolata:wght@400;500;700;900&display=swap");

  width: 20%;
  margin: 0 auto;
  padding: 1rem 2rem;
  display: flex;
  flex-direction: column;
  align-items: center;
  background-color: blue;

  div:first-of-type{
    display: flex;
    align-items: center;
    justify-content: space-between;
  }

  h1 {
    color: black;
    font-family: "Inconsolata", monospace;
    font-size: clamp(1.5rem, 2vw, 3rem);
  }

  img {
    width: 15px;
    height: 15px;
  }

  input{
    font-size: clamp(1.5rem, 2vw, 3rem);
    margin-bottom: 1rem;
    outline: none;
    border: none;
  }

  input[type="number"]::-webkit-inner-spin-button{
    -webkit-appearance: none;
  }
`;

export default class App extends Component {

  state = {
    numberOne: "",
    numberTwo: "",
    result: ""
  };

  handleChangeOne = (e) => {
    this.setState({
      numberOne: Number(e.target.value)
    })
  };

  handleChangeTwo = (e) => {
    this.setState({
      numberTwo: Number(e.target.value)
    })
  };

  soma = (e) => {
    this.setState({
      result: this.state.numberOne + this.state.numberTwo
    })
  };

  less = () => {
    this.setState({
      result: this.state.numberOne - this.state.numberTwo
    })
  };

  multi = () => {
    this.setState({
      result: this.state.numberOne * this.state.numberTwo
    })
  };

  div = () => {
    this.setState({
      result: this.state.numberOne / this.state.numberTwo
    })
  };

  clear = () => {
    this.setState({
      result: 0
    })
  };

  render(){
    return (
      <Calc>
        <div>
          <img src={logo} alt="logo" />
          <h1>Calculadora</h1>
        </div>
        <input type="number" onChange={this.handleChangeOne}/>
        <input type="number" onChange={this.handleChangeTwo}/>
        <div>
          <button onClick={this.div}>/</button>
          <button onClick={this.multi}>*</button>
          <button onClick={this.less}>-</button>
          <button onClick={this.soma}>+</button>
          <button onClick={this.clear}>C</button>
        </div>
        <h2>{this.state.result}</h2>
      </Calc>
    );
  }
}


