import React, { Component } from "react";
import styled from "styled-components";
import logo from "./hp-logo.png";

export const Calc = styled.section`
  @import url("https://fonts.googleapis.com/css2?family=Inconsolata:wght@400;500;700;900&display=swap");

  h1 {
    color: black;
    font-family: "Inconsolata", monospace;
    font-size: clamp(1.5rem, 2vw, 3rem);
  }

  img {
    width: 3vw;
  }
`;

export default class App extends Component {
  state = {
    numberOne: "",
    numberTwo: "",
    result: "",
  };

  soma = () => {
    this.setState({
      result:
        parseFloat(this.state.numberOne) + parseFloat(this.state.numberTwo),
    });
  };

  less = () => {
    this.setState({
      result: this.state.numberOne - this.state.numberTwo,
    });
  };

  multi = () => {
    this.setState({
      result: this.state.numberOne * this.state.numberTwo,
    });
  };

  div = () => {
    this.setState({
      result: this.state.numberOne / this.state.numberTwo,
    });
  };

  clear = () => {
    this.setState({
      result: "0",
    });
  };

  handleChangeOne = (e) => {
    this.setState({
      numberOne: e.target.value,
    });
  };

  handleChangeTwo = (e) => {
    this.setState({
      numberTwo: e.target.value,
    });
  };

  render() {
    return (
      <Calc>
        <img src={logo} alt="logo" />
        <h1>Calculadora</h1>
      </Calc>
    );
  }
}
