import { toHaveDisplayValue } from "@testing-library/jest-dom/dist/matchers";
import React, { Component } from "react";
import styled from "styled-components";
import logo from "./hp-logo.png";

export const Calc = styled.section`
  @import url("https://fonts.googleapis.com/css2?family=Inconsolata:wght@400;500;700;900&display=swap");

  h1 {
    color: black;
    font-family: "Inconsolata", monospace;
    font-size: clamp(1.5rem, 2vw, 3rem);
  }

  img {
    width: 3vw;
  }
`;

export default class App extends Component {

  state = {
    numberOne: "",
    numberTwo: "",
    calcHandler: "",
    result: ""
  };



  inputValue= (e) => {
    this.setState({
      numberOne: Number(e.target.value)
    });
  }

  operatorHandler = (e) => {
      let operatorInput = e.target.value
      this.setState({
        calcHandler: operatorInput
      });
    }

  calc = () => {
    if (this.state.calcHandler === "+"){
      this.setState({
        result: Number(this.inputValueOne) + Number(this.inputValueTwo)
      })
    }
  }  

  // soma = (e) => {
  //   this.setState({
  //     calcHandler: this.inputValueOne + this.inputValueTwo
  //   });
  // };

  less = () => {
    this.setState({
      result: this.state.numberOne - this.state.numberTwo
    });
  };

  multi = () => {
    this.setState({
      result: this.state.numberOne * this.state.numberTwo
    });
  };

  div = () => {
    this.setState({
      result: this.state.numberOne / this.state.numberTwo
    });
  };

  clear = () => {
    this.setState({
      result: 0
    });
  };

  handleChangeOne = (e) => {
    this.setState({
      numberOne: e.target.value
    });
  };

  handleChangeTwo = (e) => {
    this.setState({
      numberTwo: e.target.value
    });
  };

  render(){
    return (
      <Calc>
        <div>
          <img src={logo} alt="logo" />
          <h1>Calculadora</h1>
        </div>
        <input type="number" onChange={this.handleChangeOne}/>
        <input type="number" onChange={this.handleChangeTwo}/>
        <div>
          <button onClick={this.soma}>+</button>
          <button onClick={this.clear}>C</button>
          <button onClick={this.div}>/</button>
        </div>
        <h2>{this.state.result}</h2>
      </Calc>
    );
  }
}


